import React, { useState,useEffect } from "react";
import {
  Avatar,
  Button,
  Paper,
  Grid,
  Typography,
  Container,
  TextField,
} from "@material-ui/core";
import { GoogleLogin } from 'react-google-login';
import useStyles from "./styles";
import { mergeClasses } from "@material-ui/styles";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Input from "./Input";
import Icon from "./icon";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import {signin,signup} from '../../actions/auth'

const initialState={firstName:'',lastName:'',email:'',password:'',confirmPassword:''}
const Auth = () => {
  const classes = useStyles();
  const navigate =useNavigate();
  const handleShowPassword = () =>
    setShowPassword((preShowPassword) => !preShowPassword);

    const handleSubmit = (e) => {
        e.preventDefault();
        if(isSignup){
            dispatch(signup(formData,navigate))
        }
        else{
            dispatch(signin(formData,navigate))
        }
  };
  const handleChange = (e) => {
      setFormData({...formData,[e.target.name]:e.target.value})
  };
  const switchMode = () => {
    setIsSignUp((prevIsSignup) => !prevIsSignup);
    setShowPassword(false);
  };
  const googleSuccess=async (res)=>{
      const result=res?.profileObj;
      const token=res?.tokenId;
      try{
        dispatch({type:'AUTH',data:{result,token}})
        navigate('/');
      }
      catch(error){

      }
  }
  const googleFailure=(error)=>{
      console.log(error)
      console.log("Google signed in was unsuccessful. Try again later!")
  }

  const [showPassword, setShowPassword] = useState(false);
  const [isSignup, setIsSignUp] = useState(false);
  const [formData,setFormData]=useState(initialState)
  const dispatch=useDispatch();

  const handleCallbackResponse=(response)=>{
      console.log(response.credential);
  }
  useEffect(()=>{
      /* global google*/ 
      google.accounts.id.initialize({
          client_id:"860521400580-8ghvilg29at6c1bu1mnfocgch1c96c0o.apps.googleusercontent.com",
          callback:handleCallbackResponse
      });
      google.accounts.id.renderButton(
          document.getElementById('signin'),
          {theme:"outline",size:"large"}
      );

  },[])

  return (
    <Container component={"main"} maxWidth="xs">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography variant="h5">{isSignup ? "Sign Up" : "Sign in"}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {isSignup && (
              <>
                <Input
                  name="firstName"
                  label="First Name"
                  handleChange={handleChange}
                  autoFocus
                  half
                />
                <Input name="lastName" label="Last Name" handleChange={handleChange} autoFocus half /> </> )}
            <Input
              name="email"
              label="Email Address"
              handleChange={handleChange}
              type="email"
            />
            <Input
              name="password"
              label="Password"
              handleChange={handleChange}
              type={showPassword ? "text" : "password"}
              handleShowPassword={handleShowPassword}
            />
            {isSignup && (
              <Input
                name="confirmPassword"
                label="Repeat Password"
                handleChange={handleChange}
                type="password"
              />
            )}
          </Grid>

          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={handleSubmit}
          >
            {isSignup ? "Sign Up" : "Sign in"}
          </Button>

          <Button id="signin"  className={classes.googleButton} >

          </Button>



          <Grid container justifyContent="flex-end">
            <Grid item>
              <Button onClick={switchMode}>
                {isSignup
                  ? "Already have an account? Sign in"
                  : "Don't have an account? Sign Up"}
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  );
};

export default Auth;
