import {FETCH_ALL,UPDATE,DELETE,LIKE, CREATE, FETCH_BY_SEARCH} from '../constants/actionTypes';
export default (state = [], action) => {
  switch (action.type) {
    case FETCH_ALL:
      return {...state,posts:action.payload,currentPage:action.payload.currentPage,numberOfPages:action.payload.numberOfPages}.action;
    case FETCH_BY_SEARCH:
      return {...state,posts:action.payload};
    case CREATE:
      return {...state,posts:action.payload};
    case UPDATE:
    case LIKE:
       return state.map((post)=>post._id===action.payload._id?action.payload:post);
    case DELETE:
      return state.filter((post)=>post._id!==action.payload);
    default:
      return state;
  }
};
